INSERT INTO `question` VALUES (1,'1','1','몇 가지 종류의 액상을 사용하십니까?','0',NULL,0,0,0,NULL,NULL,NULL,NULL,'2024-05-11 06:13:06','2024-05-15 13:26:05',NULL,NULL,NULL),(2,'2','1','액상 각각의 액형은 무엇입니까?','0',NULL,1,0,0,NULL,NULL,NULL,NULL,'2024-05-11 06:13:06','2024-05-15 13:26:05',NULL,NULL,NULL),(3,'2-1','0','귀하의 액상 혼합비는 얼마인가요?','0',NULL,0,0,4,NULL,NULL,NULL,'1','2024-05-11 06:13:06','2024-05-11 15:29:18',NULL,NULL,NULL),(4,'2-2','0','2액형 카트리지를 사용하시나요?','0',NULL,0,0,0,'image','듀얼카트리지','static/A8187207-0F2D-4DDC-8A82-E415379E7A28.png',NULL,'2024-05-11 06:13:06','2024-06-03 18:55:14',NULL,NULL,NULL),(5,'3','0','액상을 사용하는 APPLICATION은 무엇입니까?','0',NULL,0,0,0,NULL,NULL,NULL,'LINE_DISPENSING','2024-05-11 06:13:06','2024-06-03 12:54:37',NULL,NULL,NULL),(6,'4','0','액상의 점도는 얼마입니까? ','0',NULL,1,0,19,'text','점도(Viscosity)란?','액체의 흐름성의 척도로, 점도가 높을수록 액체가 잘 흐르지 않습니다.\n1CPS는 물의 점도를 기준으로 합니다.',NULL,'2024-05-11 06:13:06','2024-06-03 18:27:45','static/viscosity.png',NULL,NULL),(7,'5','0','액상의 비중은 얼마인가요?','0',NULL,1,0,0,'text','비중(Specific Gravity)란?','액체의 무게를 물의 무게와 비교한 것으로, 같은 부피(L)의 액체와 \\n물의 무게(kg)을 비교하여 수치화합니다. 물의 비중 1을 기준으로 합니다.','1','2024-05-11 06:13:06','2024-06-02 03:51:53',NULL,NULL,NULL),(8,'6','0','각각의 토출량(Tack Time)은 얼마입니까?','0',NULL,0,0,0,NULL,NULL,NULL,'EveryThing','2024-05-11 06:13:06','2024-06-03 13:06:35',NULL,NULL,NULL),(9,'7','0','토출 정밀도는 얼마입니까?','0',NULL,0,0,0,NULL,NULL,NULL,'10','2024-05-11 06:13:06','2024-06-03 13:06:35',NULL,NULL,NULL),(10,'8','0','액상에 FILLER는 포함되어 있습니까?','0',NULL,1,0,0,'text','FILLER란?','액체 내에 있는 고체 입자를 의미합니다.','NO','2024-05-11 06:13:06','2024-06-02 09:18:16',NULL,NULL,NULL),(11,'9','0','액상 경화조건(Curing Condition)은 무엇입니까?','0',NULL,1,1,0,'text','경화(Curing)란?','액체가 굳음을 의미합니다.','자연 경화 ROOM_TEMPERATURE','2024-05-11 06:13:06','2024-06-03 11:57:02',NULL,NULL,''),(12,'10-1','1','액 재료의 제공 형태는 무엇입니까?','0',NULL,1,0,13,NULL,NULL,NULL,NULL,'2024-05-11 06:13:06','2024-06-03 16:37:51',NULL,NULL,NULL),(13,'10-2','0','액 재료의 용량은 어떻게 됩니까?','0',NULL,1,0,0,NULL,NULL,NULL,'3','2024-05-11 06:13:06','2024-06-03 16:37:51',NULL,NULL,NULL),(14,'11','0','로봇 구성은 어떻게 할 것입니까?','0',NULL,0,0,0,NULL,NULL,NULL,'MANUAL','2024-05-11 06:13:06','2024-05-11 15:31:33',NULL,NULL,NULL),(15,'11-1','0','액상을 토출할 제품의 크기는 얼마입니까?','0',NULL,0,0,0,'image',NULL,NULL,'20,20,5','2024-05-11 06:13:06','2024-06-02 12:04:20','static/item11.png',NULL,NULL),(16,'12','0','완성된 구성을 최대한 저렴하게 할 것인가? 편리성을 생각하여 자동화를 할 것 입니까?','0',NULL,0,0,0,NULL,NULL,NULL,'LOW PRICE','2024-05-11 06:13:06','2024-06-03 13:08:16',NULL,-1,NULL),(19,'4-1','1','액체의 형태가 Paste입니까?','0',NULL,1,0,0,'text','Paste란?','흐름성이 없는 액체로, 고체와 유사합니다.\nPaste 예시로 치약, 케찹, 마요네즈 등이 있습니다.',NULL,'2024-06-03 09:54:49','2024-06-03 13:25:31',NULL,7,NULL);
